import React,{useState, useEffect} from 'react'
import axios from "axios";
import cityTimezones from "city-timezones";
import Weathercard from './Weathercard';

function Weather() {
    const clu = {
        name : "Kolkata"
    }
    const [city, setCity] = useState("kolkata")
    const [tempInfo, settempInfo] = useState({})
    // time differentlocation
    const [cityLookup, setCityLookup] = useState(cityTimezones.lookupViaCity(clu))
    const [background, setBackground] = useState("#fafafa")
    const getWeatherInfo = () => {
        axios(
            {
                url: `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=f610b69b3c6d7eb99d61f54215f3362e
                `,
                method: 'GET',
                headers : { 'Content-Type':'application/json' }
            }
        ).then(
            response => {
                const data = response.data;
                const {temp, humidity, pressure } = data.main;
                const {main: weatherMood} = data.weather[0];
                const {name} = data;
                // const {timezone} = data;
                const {speed} = data.wind;
                const {country, sunset} = data.sys;

                const myWeatherInfo = {
                    temp,
                    humidity,
                    pressure,
                    weatherMood,
                    name,
                    speed,
                    country,
                    sunset
                    // timezone
                }
                settempInfo(myWeatherInfo)
                // background change
                setCityLookup(cityTimezones.lookupViaCity(myWeatherInfo.name));
                console.log(`actual value ${myWeatherInfo.name} and citylookup value : ${cityLookup}`)
                const timeNow = new Date().toLocaleTimeString('en-GB', {timeZone : cityLookup[0].timezone })
                console.log(timeNow)
                "5:00:00" > timeNow > "16:00:00" ? setBackground("#191970"): setBackground("fafafa");
                console.log(`true or false : ${"5:00:00" > timeNow > "16:00:00"}`)
                console.log(background)
                // background change
            }
        ).catch((error) => {console.log(error)})
    }
    useEffect(() => {
        getWeatherInfo();
    },[])

    return (
        <>
            <div className="container">
                <div className="wrap">
                    <div className="search">
                        <input type="search" placeholder="Search..." className="searchTerm" autoFocus id="search"
                            value={city} onChange={(e)=>{setCity(e.target.value)}}
                        />
                        <button className="searchButton" onClick={getWeatherInfo}>Search</button>
                    </div>
                </div>
                <Weathercard tempInfo = {tempInfo} background = {background}/>
            </div>
        </>
    )
}

export default Weather;
